fun calculatePercentage(dayData) = 
    if (isEmpty(dayData)) []
    else dayData map ((item) ->
        {
            nrRozdania: item.nrRozdania,
            wartosc: item.wartosc,
            proc: (item.wartosc* 100) / maxBy(dayData, (i) -> i.wartosc).wartosc
        }
    )

var zapisy= readUrl("classpath://zapis.json","application/json") 
var zapisyZDnia = zapisy filter ($.turniej.dataTurnieju == vars.dataTurnieju)

// Procent jest żle obliczany - bo bierze wartości pod uwagę a nie punkty na maxy, 
// najpierw przydzielić punkty a dopiero pózniej policzyć procent.
---
zapisyZDnia.turniej[0].zapis groupBy $.nrRozdania pluck ((value, key) -> {
	nrRozdania: key,
    data: calculatePercentage(value)
})

%dw 2.0 output application/json var wysKontraktu=vars.kontrakt[0] as Number var jestKontra=vars.kontrakt contains "X" var jestRekontra=vars.kontrakt contains "XX" var kolor=vars.kontrakt[1] var piKier=(vars.kontrakt[1]=="P") or (vars.kontrakt[1]=="C") var kaTrefl=(vars.kontrakt[1]=="K") or (vars.kontrakt[1]=="T") var jestBez=vars.kontrakt contains "-" var ileBez=if(jestBez) (vars.kontrakt match /-(\d)/)[1] as Number else 0 var saNadrobki = vars.kontrakt contains "+" var ileNadrobek=if(saNadrobki)(vars.kontrakt match /\+(\d+)/)[1] as Number else 0 var zaBezZKontra=100 var podKreska= 2*if (ileBez==0) if (piKier) wysKontraktu*30 else if (kaTrefl) wysKontraktu*20 else 40+(wysKontraktu-1)*30 else 0 var podKreskaZKontra=if (jestKontra) podKreska*2 else podKreska var podKreskaZRekontra=if (jestRekontra) podKreskaZKontra*2 else podKreskaZKontra var premiaZaPartie= if (podKreskaZRekontra>100) 300 else 0 var zaNadrobki= if (jestKontra) ileNadrobek*100 else if (kaTrefl) ileNadrobek*20 else ileNadrobek*30 var zaNieudanaKontre=if (jestKontra and ileBez==0) 50 else 0 var zaBez= if (jestKontra) if (ileBez<4) 100 + (ileBez-1)*200 else 800+(ileBez-4)*300 else ileBez*50
---
podKreskaZRekontra+zaNadrobki+zaNieudanaKontre+zaBez+premiaZaPartie as Number {encoding: "UTF-8", mediaType: "application/json" }
